import ExpenseItem from "./ExpenseItem"
import Card from "../UI/Card"
import "./Expenses.css"
import ExpensesFilter from "./ExpensesFilter"

import { useState } from "react";

function Expenses ( props )
{
    let expanses = props.items;
    
    const [filteredYear,setFilteredYear] = useState("2020");

    const FilterValueChangeHandler = ( selectedYear ) =>
    {
        console.log(selectedYear);
        setFilteredYear(selectedYear);
    }
    return (
     <div>
    <Card className="expenses"> 
    <ExpensesFilter filterValue={filteredYear} onFilterValueChanged = {FilterValueChangeHandler}></ExpensesFilter>
    <ExpenseItem title={expanses[0].title} amount={expanses[0].amount} date={expanses[0].date}></ExpenseItem>
    <ExpenseItem title={expanses[1].title} amount={expanses[1].amount} date={expanses[1].date}></ExpenseItem>
    <ExpenseItem title={expanses[2].title} amount={expanses[2].amount} date={expanses[2].date}></ExpenseItem>
    </Card>
    </div>
    );
}

export default Expenses;
