import ExpenseItem from "./ExpenseItem"
import Card from "../UI/Card"
import "./Expenses.css"
import ExpensesFilter from "./ExpensesFilter"

import { useState } from "react";


function Expenses ( props )
{
    let expanses = props.items;
    
    const [filteredYear,setFilteredYear] = useState("2020");

    const FilterValueChangeHandler = ( selectedYear ) =>
    {
        console.log(selectedYear);
        expanses = expanses.map((expanse) => {if(expanse.date.getFullYear() == selectedYear) return expanse});
        setFilteredYear(selectedYear);
        
    }
    return (
     <div>
    <Card className="expenses"> 
    <ExpensesFilter filterValue={filteredYear} onFilterValueChanged = {FilterValueChangeHandler}></ExpensesFilter>

    {expanses.length === 0 ? <p>No expenses found</p> : expanses.map(expanse => <ExpenseItem key={expanse.id} title={expanse.title} amount={expanse.amount} date={expanse.date}/>)}

    </Card>
    </div>
    );
}

export default Expenses;
