import styles from "./Button.module.css"



const Button = ( props ) => 
{

    const ButtonClickedAnimation = () =>
    {
    
        let rippleElements = document.getElementsByClassName(`${styles["button"]}`);
    
        for(var i = 0; i < rippleElements.length; i++) {
            console.log("girdim")
            rippleElements[i].onclick = function(e) {
                let X = e.pageX - this.offsetLeft;
                let Y = e.pageY - this.offsetTop;
                let rippleDiv = document.createElement("div");
                
                rippleDiv.classList.add('ripple');
                rippleDiv.setAttribute("style","top:"+Y+"px; left:"+X+"px;");
                let customColor = this.getAttribute('ripple-color');
                if(customColor) rippleDiv.style.background = customColor;
                this.appendChild(rippleDiv);
                setTimeout(function(){
                  rippleDiv.parentElement.removeChild(rippleDiv);
                }, 900);
          }
        }
    }

    return(
    <div  ripple-color='red' onClick={ButtonClickedAnimation} className={`${styles["button"]}`}>
        <span></span>
        <span></span>
        <span></span>
        <span></span>
        <button type={props.type} onClick={props.onClick}>
            {props.children}
        </button>
    </div>
    );
}

export default Button;
