import styles from "./Navbar.module.css";
import logo from "../icons/icon.png"
import $ from "jquery";

const Navbar = ( props ) => 
{
    const MouseClickHandler = () => 
    {
        console.log("gird1i")

        $(`${styles["companyName"]}`).each(function(){
            console.log("girdi")
            $(this).css('animation',"nameAnim 550ms ease forwards");
            setTimeout(()=> {            $(this).css('animation',"");        },550);

        });
    }
    const MouseEnterHandler = () =>
    {
        $(`.${styles["menu"]} > li`).each(function(){
           let wid = $(this).width();
           let height = $(this).height();
            $(this).css('width', wid+'px');
            $(this).css('height',height+"px");
        });
        
    }
    return(
    <nav className={`${styles["navbar"]}`}>
        <div>
        <img alt="qwenwq" src={logo} width="60" height="20">
        </img>
        <label className={`${styles["companyName"]}`}>Teknik Servis</label>
        </div>
        <div className={`${styles["rightNavbar"]}`}>
            <ul className={`${styles["menu"]}`}>
                <li onClick={MouseClickHandler} onMouseEnter={MouseEnterHandler}>Home</li>
                <li>Services</li> 
                <li>About</li> 
                <li>Contact</li> 
            </ul>
        </div>
    </nav>
    );
}

export default Navbar;
